    #[doc = ""]
    #[doc = " The ::CUDA_RESOURCE_DESC structure is defined as:"]
    #[doc = " \\code"]
    #[doc = "typedef struct CUDA_RESOURCE_DESC_st"]
    #[doc = "{"]
    #[doc = "CUresourcetype resType;"]
    #[doc = ""]
    #[doc = "union {"]
