    #[doc = "CUdeviceptr devPtr;"]
    #[doc = "CUarray_format format;"]
    #[doc = "unsigned int numChannels;"]
    #[doc = "size_t sizeInBytes;"]
    #[doc = "} linear;"]
    #[doc = "struct {"]
    #[doc = "CUdeviceptr devPtr;"]
    #[doc = "CUarray_format format;"]
    #[doc = "unsigned int numChannels;"]
    #[doc = "size_t width;"]
    #[doc = "size_t height;"]
    #[doc = "size_t pitchInBytes;"]
    #[doc = "} pitch2D;"]
    #[doc = "} res;"]
    #[doc = ""]
    #[doc = "unsigned int flags;"]
