    #[doc = " where:"]
    #[doc = " - ::CUDA_RESOURCE_DESC::resType specifies the type of resource to texture from."]
    #[doc = " CUresourceType is defined as:"]
    #[doc = " \\code"]
    #[doc = "typedef enum CUresourcetype_enum {"]
